SELECT        Tb_Semestre.Semestre, Tb_Ano.Ano, Tb_Curso.Curso, Tb_Disciplina.Disciplina
FROM            Tb_Disciplina INNER JOIN
                         Tb_Curso ON Tb_Disciplina.Id_Curso = Tb_Curso.Id_Curso INNER JOIN
                         Tb_Ano ON Tb_Disciplina.Id_Ano = Tb_Ano.Id_Ano INNER JOIN
                         Tb_Semestre ON Tb_Disciplina.Id_Semestre = Tb_Semestre.Id_Semestre